export const DEVICE_WIDTH = Dimensions.get('window').width;
export const DEVICE_HEIGHT = Dimensions.get('window').height;
export const TEXTSIZE = 18;
export const MARGIN_VIEW = 20;
export const PADDING_CONTENT = 10;
